mapper.select:
select br.boardIdx, br.memberId, br.boardRecommendCheck
	from board b join boardRecommend br on b.boardIdx=br.boardIdx
		where br.boardIdx=#{boardIdx} and br.memberId=#{memberId}

select rownum rno, boardidx, boardtitle, memberid, boardhits, boarddate 
	from (select boardidx, boardtitle, memberid, boardhits, to_char(boarddate,'yyyy/mm/dd') boarddate
		from(select boardidx, boardtitle, memberid, boardhits, boarddate 
			from board order by boarddate desc
			)
		)
