mapper.update:
update cgv_board

	
		set btitle=#{btitle}, bcontent=#{bcontent}, 
			bfile=#{bfile}, bsfile=#{bsfile}
	
	
		set btitle=#{btitle}, bcontent=#{bcontent}
	

 where bid=#{bid}

mapper.select:
select rno, bid, btitle, bhit, bdate 
from (select rownum rno, bid, btitle, bhit, 
		to_char(bdate,'yyyy/mm/dd') bdate
	from(select bid, btitle, bhit, bdate from  cgv_board order by bdate desc)
 ) where rno between #{start} and #{end}
